package ejerciciosfor;

import java.util.Scanner;

public class Ejercicio02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Se formará un bucle que crea una variable con valor a 200 que 
		//decrementará en 2 con cada vuelta y terminará cuando el numero sea menor que 2
		
		for(int numero = 200; numero >= 2; numero= numero-2) {
		System.out.println(numero);
		}
	}
}