package ejerciciosfor;

import java.util.Scanner;

public class Ejercicio03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Se formará un bucle que crea una variable con valor a 7 que 
		//decrementará en 7 con cada vuelta y terminará cuando el numero sea mayor que 100
		for(int numero = 7; numero <= 100; numero= numero + 7) {
		System.out.println(numero);
		}
	}
}